$(window).scroll(function(e) {
  var height = $(this).scrollTop();
  $('.header-fixed')[height >= 200 ? 'addClass' : 'removeClass']
  ('fixed').text()
});

$(function(){
  $('a[href^="#"]').on('click', function(event) {
    event.preventDefault();
    var sc = $(this).attr("href"),
        dn = $(sc).offset().top;
    $('html, body').animate({scrollTop: dn}, 1000);
  });
});